    color: var\(--text-primary\);
    margin-bottom: 1.5rem;
    text-align: center;
}

.rating-stats {
    display: flex;
    flex-direction: column;
    gap: 1rem;
}

.rating-stat {
    display: flex;
    flex-direction: column;
    gap: 0.25rem;
}

.stat-emoji {
    font-size: 1.5rem;
}

.stat-label {
    font-size: 0.85rem;
    color: var\(--text-secondary\);
    font-weight: 600;
}

.stat-value {
    font-size: 1.25rem;
    font-weight: 700;
    color: var\(--text-primary\);
}

.stat-bar {
    width: 100%;
    height: 8px;
    background: var\(--bg-section\);
    border-radius: 4px;
    overflow: hidden;
    margin-top: 0.25rem;
}

.stat-bar-fill {
    height: 100%;
    transition: width 0.3s ease;
}

.stat-bar-fill.useful {
    background: var\(--accent-green\);
}

.stat-bar-fill.not-useful {
    background: var\(--accent-orange\);
}

.stat-bar-fill.banned {
    background: var\(--brand-red\);
}

.rating-total {
    text-align: center;
    margin-top: 1rem;
    font-size: 0.85rem;
    color: var\(--text-secondary\);
    font-weight: 600;
}

.loading-text,
.no-ratings {
    text-align: center;
    color: var\(--text-secondary\);
    font-style: italic;
    padding: 2rem;
}

/* Mobile Responsive */
@media \(max-width: 768px\) {
    .rating-buttons {
        flex-direction: column;
    }

    .rating-btn {
        min-width: 100%;
    }

    .ratings-grid {
        grid-template-columns: 1fr;
    }
}
